import "../App.css";
import { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import * as BookAPI from "../BooksAPI";

const Search = () => {
  const [data, setData] = useState([]);
  const [selectedValue, setSelectedValue] = useState('');
  const handleSelectChange = (event) => {
    setSelectedValue(event.target.value);
  };
  const bookshelf = [
    { id: 0, name: "moveTo..." },
    { id: 1, name: "currentlyReading" },
    { id: 2, name: "wantToRead" },
    { id: 3, name: "read" },
    { id: 4, name: "none" },
  ];

  useEffect(() => {
    const fetchData = async () => {
      try {
        const booksData = await BookAPI.getAll();
        console.log(booksData);
        setData(booksData);
      } catch (error) {
        console.log("Error fetching books:", error);
      }
    };

    fetchData();
  }, []);
  return (
    <div className="search-books">
      <div className="search-books-bar">
        <Link to="/" className="close-search">
          Close
        </Link>
        <div className="search-books-input-wrapper">
          <input type="text" placeholder="Search by title, author, or ISBN" />
        </div>
      </div>
      <div className="search-books-results">
        <ol className="books-grid">
          {data.map((filterBooks) => (
            <li key={filterBooks.id}>
              <div className="book">
                <div className="book-top">
                  <img
                    className="book-cover"
                    src={filterBooks.imageLinks.thumbnail}
                    alt={filterBooks.title}
                  />
                  <div className="book-shelf-changer">
                    <select value={selectedValue} onChange={handleSelectChange}>
                        <option value="">Move To..</option>
                        {bookshelf.map((bookshelf,id)=>(
                            <option key={id} value={bookshelf}>{bookshelf}</option>

                        ))}
              
                    </select>
                  </div>
                </div>
                <div className="book-title">{filterBooks.title}</div>
                <div className="book-authors">{filterBooks.authors}</div>
              </div>
            </li>
          ))}
        </ol>
      </div>
    </div>
  );
};

export default Search;
