import "./App.css";
import { useState,useEffect } from "react";
import { Route, Routes } from "react-router-dom";
import Search from "./components/Search";
import MyBooks from "./components/MyBooks";
import * as BookAPI from "./BooksAPI";
import Books from "./components/Books";

function App() {
  const [books, setBooks] = useState([]);

  const options = [
    { name: "Currently Reading", id: "currentlyReading" },
    { name: "Want to Read", id: "wantToRead" },
    { name: "Read", id: "read" },
  ];
  const bookShelfs = [
    { id: 1, name: "currently Reading" },
    { id: 2, name: "want To Read" },
    { id: 3, name: "read" },
    { id: 4, name: "none" },
  ];
  useEffect(() => {
    const fetchData = async () => {
      const result = await BookAPI.getAll();
      setData(result);
    };
    fetchData();
  }, []);
  const changeShelf = (book, shelf) => {
    book.shelf = shelf;
    BookAPI.update(book, shelf).then(() => {
      BookAPI.getAll().then((books) => setData(books));
    });
    setData([...data.filter((b) => b.id !== data.id), data]);
  };
  return (
    <div className="app">
      <Routes>
        <Route exact path="/Search" element={<Search options={options} />} />
        <Route
          exact
          path=""
          element={
          <MyBooks
          data={data}
          changeShelf={changeShelf}
          options={options}
        />
          }
        />
      </Routes>
    </div>
  );
}

export default App;
